definition.script:
pipeline {
    agent any

    environment {
        GITHUB_REPO = 'https://github.com/hadabas/prf-konyvklub.git'
        BRANCH = 'devops'
        CLIENT_CONTAINER = 'client_container'
        SERVER_CONTAINER = 'server_container'
    }

    stages {
        stage ('Kliens Build & Deploy') {
            steps {
                echo 'Kliens Deployol√°s folyamatban..'
                sshagent(credentials: ['jenkins-deploy-key']) {
                    sh """
                        ssh -o StrictHostKeyChecking=no deploy@${env.CLIENT_CONTAINER} -p 22 '
                            sudo /usr/sbin/rsyslogd
                            cd /app
                            rm -rf *
                            git clone ${env.GITHUB_REPO}
                            cd /app/prf-konyvklub/
                            git checkout origin/devops
                            cd client
                            npm install
                            pm2 start "ng serve --host 0.0.0.0 --port 4200 --disable-host-check" --name Client --output /var/log/pm2/out.log --error /var/log/pm2/error.log
                    '
                    """
                }
            }
        }
    }
}
